        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/LRT_Omega",                                 255, 100, 0, 1, 0, "LRT Omega",                                     "", "LRT Omega", "estimated nonsynonymous-to-synonymous-rate ratio (Omega, reported by LRT).", "VARCHAR(200)", "LRT Omega||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/LRT_Ranked_Score",                          255, 100, 0, 1, 0, "LRT Ranked Score",                              "", "LRT Ranked Score", "LRTori scores were first converted as LRTnew=1-LRTori*0.5 if Omega<1, or LRTnew=LRTori*0.5 if Omega>=1. Then LRTnew scores were ranked among all LRTnew scores in dbNSFP. The rankscore is the ratio of the rank over the total number of the scores in dbNSFP. The scores range from 0.00166 to 0.85682.", "VARCHAR(200)", "LRT Ranked Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
