        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MetaLR",                                    255, 100, 0, 1, 0, "MetaLR Prediction",                             "", "MetaLR Prediction", "Prediction of our MetaLR based ensemble prediction score,T(olerated) or D(amaging). The score cutoff between D and T is 0.5. The rankscore cutoff between D and T is 0.82268..", "VARCHAR(200)", "MetaLR Prediction||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MetaLR_Ranked_Score",                       255, 100, 0, 1, 0, "MetaLR Ranked Score",                           "", "MetaLR Ranked Score", "MetaLR scores were ranked among all MetaLR scores in dbNSFP. The rankscore is the ratio of the rank of the score over the total number of MetaLR scores in dbNSFP. The scores range from 0 to 1..", "VARCHAR(200)", "MetaLR Ranked Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MetaLR_Score",                              255, 100, 0, 1, 0, "MetaLR Score",                                  "", "MetaLR Score", "Our logistic regression (LR) based ensemble prediction score, which incorporated 10 scores (SIFT, PolyPhen-2 HDIV, PolyPhen-2 HVAR, GERP++, MutationTaster, Mutation Assessor, FATHMM, LRT, SiPhy, PhyloP) and the maximum frequency observed in the 1000 genomes populations. Larger value means the SNV is more likely to be damaging. Scores range from 0 to 1.", "VARCHAR(200)", "MetaLR Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MetaSVM",                                   255, 100, 0, 1, 0, "MetaSVM Prediction",                            "", "MetaSVM Prediction", "Prediction of our SVM based ensemble prediction score,T(olerated) or D(amaging). The score cutoff between D and T is 0. The rankscore cutoff between D and T is 0.83357..", "VARCHAR(200)", "MetaSVM Prediction||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MetaSVM_Ranked_Score",                      255, 100, 0, 1, 0, "MetaSVM Ranked Score",                          "", "MetaSVM Ranked Score", "MetaSVM scores were ranked among all MetaSVM scores in dbNSFP. The rankscore is the ratio of the rank of the score over the total number of MetaSVM scores in dbNSFP. The scores range from 0 to 1..", "VARCHAR(200)", "MetaSVM Ranked Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MetaSVM_Score",                             255, 100, 0, 1, 0, "MetaSVM Score",                                 "", "MetaSVM Score", "Our support vector machine (SVM) based ensemble prediction score, which incorporated 10 scores (SIFT, PolyPhen-2 HDIV, PolyPhen-2 HVAR, GERP++, MutationTaster, Mutation Assessor, FATHMM, LRT, SiPhy, PhyloP) and the maximum frequency observed in the 1000 genomes populations. Larger value means the SNV is more likely to be damaging. Scores range from -2 to 3 in dbNSFP.", "VARCHAR(200)", "MetaSVM Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MutationAssessor",                          255, 100, 0, 1, 0, "Mutation Assessor Prediction",                  "", "Mutation Assessor Prediction", "MutationAssessor\'s functional impact of a variant : predicted functional, i.e. high (H) or medium (M), or predicted non-functional, i.e. low (L) or neutral (N). The MAori score cutoffs between H and M, M and L, and L and N, are 3.5, 1.935 and 0.8, respectively. The rankscore cutoffs between H and M, M and L, and L and N, are 0.92924, 0.51945 and 0.19692, respectively.", "VARCHAR(200)", "Mutation Assessor Prediction||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MutationAssessor_Ranked_Score",             255, 100, 0, 1, 0, "Mutation Assessor Ranked Score",                "", "Mutation Assessor Ranked Score", "MAori scores were ranked among all MAori scores in dbNSFP. The rankscore is the ratio of the rank of the score over the total number of MAori scores in dbNSFP. The scores range from 0 to 1..", "VARCHAR(200)", "Mutation Assessor Ranked Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MutationAssessor_Score",                    255, 100, 0, 1, 0, "Mutation Assessor Score",                       "", "Mutation Assessor Score", "MutationAssessor functional impact combined score (MAori). The score ranges from -5.135 to 6.49 in dbNSFP. Please refer to Reva et al. (2011) Nucl. Acids Res. 39(17):e118 for details..", "VARCHAR(200)", "Mutation Assessor Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MutationTaster_Ranked_Score",               255, 100, 0, 1, 0, "Mutation Taster Ranked Score",                  "", "Mutation Taster Ranked Score", "The MTori scores were first converted: if the prediction is A or D MTnew=MTori; if the prediction is N or P, MTnew=1-MTori. Then MTnew scores were ranked among all MTnew scores in dbNSFP. The rankscore is the ratio of the rank of the score over the total number of MTnew scores in dbNSFP. The scores range from 0.09067 to 0.80722..", "VARCHAR(200)", "Mutation Taster Ranked Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MutationTaster",                            255, 100, 0, 1, 0, "MutationTaster prediction",                     "", "MutationTaster prediction.", "MutationTaster prediction, A (disease_causing_automatic), D (disease_causing), N (polymorphism) or P (polymorphism_automatic). The score cutoff between D and N is 0.5 for MTori and 0.31655 for the rankscore.", "VARCHAR(200)", "MutationTaster prediction||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MutationTaster_Score",                      255, 100, 0, 1, 0, "MutationTaster score",                          "", "MutationTaster score.", "MutationTaster p-value (MTori), ranges from 0 to 1.", "VARCHAR(255)", "MutationTaster score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/PROVEAN_Ranked_Score",                      255, 100, 0, 1, 0, "PROVEAN Ranked Score",                          "", "PROVEAN Ranked Score", "PROVEANori were first converted to PROVEANnew=1-(PROVEANori+14)/28, then ranked among all PROVEANnew scores in dbNSFP. The rankscore is the ratio of the rank the PROVEANnew score over the total number of PROVEANnew scores in dbNSFP. If there are multiple scores, only the most damaging (largest) rankscore is presented..", "VARCHAR(200)", "PROVEAN Ranked Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/PROVEAN",                                   255, 100, 0, 1, 0, "PROVEAN Prediction",                            "", "PROVEAN Prediction", "If PROVEANori <= -2.5 (rankscore>=0.59) the corresponding NS is predicted as D(amaging); otherwise it is predicted as N(eutral). Multiple predictions separated by ;.", "VARCHAR(200)", "PROVEAN Prediction||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/PROVEAN_Score",                             255, 100, 0, 1, 0, "PROVEAN Score",                                 "", "PROVEAN Score", "PROVEAN score (PROVEANori). Scores range from -14 to 14. The smaller the score the more likely the SNP has damaging effect. Multiple scores separated by ;. Details can be found in DOI: 10.1371/journal.pone.0046688.", "VARCHAR(200)", "PROVEAN Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
