        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MutationAssessor_Score",                    255, 100, 0, 1, 0, "Mutation Assessor Score",                       "", "Mutation Assessor Score", "MutationAssessor functional impact combined score (MAori). The score ranges from -5.135 to 6.49 in dbNSFP. Please refer to Reva et al. (2011) Nucl. Acids Res. 39(17):e118 for details..", "VARCHAR(200)", "Mutation Assessor Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MutationTaster_Ranked_Score",               255, 100, 0, 1, 0, "Mutation Taster Ranked Score",                  "", "Mutation Taster Ranked Score", "The MTori scores were first converted: if the prediction is A or D MTnew=MTori; if the prediction is N or P, MTnew=1-MTori. Then MTnew scores were ranked among all MTnew scores in dbNSFP. The rankscore is the ratio of the rank of the score over the total number of MTnew scores in dbNSFP. The scores range from 0.09067 to 0.80722..", "VARCHAR(200)", "Mutation Taster Ranked Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/MutationTaster",                            255, 100, 0, 1, 0, "MutationTaster prediction",                     "", "MutationTaster prediction.", "MutationTaster prediction, A (disease_causing_automatic), D (disease_causing), N (polymorphism) or P (polymorphism_automatic). The score cutoff between D and N is 0.5 for MTori and 0.31655 for the rankscore.", "VARCHAR(200)", "MutationTaster prediction||select|1|true|false|false", "A\r\nD\r\nN\r\nP", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
