        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/PROVEAN_Ranked_Score",                      255, 100, 0, 1, 0, "PROVEAN Ranked Score",                          "", "PROVEAN Ranked Score", "PROVEANori were first converted to PROVEANnew=1-(PROVEANori+14)/28, then ranked among all PROVEANnew scores in dbNSFP. The rankscore is the ratio of the rank the PROVEANnew score over the total number of PROVEANnew scores in dbNSFP. If there are multiple scores, only the most damaging (largest) rankscore is presented..", "VARCHAR(200)", "PROVEAN Ranked Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/PROVEAN",                                   255, 100, 0, 1, 0, "PROVEAN Prediction",                            "", "PROVEAN Prediction", "If PROVEANori <= -2.5 (rankscore>=0.59) the corresponding NS is predicted as D(amaging); otherwise it is predicted as N(eutral). Multiple predictions separated by ;.", "VARCHAR(200)", "PROVEAN Prediction||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
