        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/Polyphen2_HDIV",                            255, 100, 0, 1, 0, "Polyphen2 HDIV Prediction",                     "", "Polyphen2 HDIV Prediction", "long.", "VARCHAR(200)", "Polyphen2 HDIV Prediction||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
