        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/Polyphen2_HDIV_Score",                      255, 100, 0, 1, 0, "Polyphen2 HDIV Score",                          "", "Polyphen2 HDIV Score", "Polyphen2 score based on HumDiv, i.e. hdiv_prob. The score ranges from 0 to 1. Multiple entries separated by ;..", "VARCHAR(200)", "Polyphen2 HDIV Score||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
        'INSERT INTO ' . TABLE_COLS . ' VALUES ("VariantOnTranscript/Prediction/Polyphen2_HVAR",                            255, 100, 0, 1, 0, "Polyphen2 HVAR Prediction",                     "", "Polyphen2 HVAR Prediction", "Polyphen2 prediction based on HumVar, D (porobably damaging, HVAR score in [0.909,1] or rankscore in [0.62955,0.9711]), P (possibly damaging, HVAR in [0.447,0.908] or rankscore in [0.44359,0.62885]) and B (benign, HVAR score in [0,0.446] or rankscore in [0.01281,0.44315]). Score cutoff for binary classification is 0.5 for HVAR score or 0.45998 for rankscore, i.e. the prediction is neutral if the HVAR score is smaller than 0.5 (rankscore is smaller than 0.45998), and deleterious if the HVAR score is larger than 0.5 (rankscore is larger than 0.45998). Multiple entries are separated by ;..", "VARCHAR(200)", "Polyphen2 HVAR Prediction||text|6", "", "", 0, 0, 1, 0, NOW(), NULL, NULL)',
